// frontend/src/components/UploadPanel.jsx
import React, {useState} from "react";
import { uploadPdf } from "../api";

export default function UploadPanel({ token, onUploaded }) {
  const [companyName, setCompanyName] = useState("");
  const [file, setFile] = useState(null);
  const [pdfUrl, setPdfUrl] = useState("");
  const [status, setStatus] = useState("");

  async function doUpload(){
    setStatus("Uploading...");
    try {
      const res = await uploadPdf(token, companyName, file, pdfUrl);
      onUploaded(res.data);
      setStatus("Done");
    } catch(e){
      setStatus("Error: " + (e.response?.data?.detail || e.message));
    }
  }

  return (
    <div>
      <div>
        <label>Company name</label>
        <input value={companyName} onChange={e=>setCompanyName(e.target.value)} placeholder="Reliance Consolidated" />
      </div>
      <div>
        <label>PDF URL (optional)</label>
        <input value={pdfUrl} onChange={e=>setPdfUrl(e.target.value)} placeholder="https://..." />
      </div>
      <div>
        <label>Or upload PDF</label>
        <input type="file" accept="application/pdf" onChange={e=> setFile(e.target.files[0])} />
      </div>
      <div>
        <button onClick={doUpload}>Process</button>
        <div>{status}</div>
      </div>
    </div>
  );
}
