// frontend/src/App.jsx
import React, {useState, useEffect} from "react";
import Login from "./components/Login";
import UploadPanel from "./components/UploadPanel";
import Dashboard from "./components/Dashboard";
import { listCompanies } from "./api";

function App(){
  const [token, setToken] = useState(localStorage.getItem("bsa_token") || null);
  const [result, setResult] = useState(null);
  const [companies, setCompanies] = useState([]);

  useEffect(()=>{
    if (token) {
      localStorage.setItem("bsa_token", token);
      loadCompanies();
    } else {
      localStorage.removeItem("bsa_token");
    }
  }, [token]);

  async function loadCompanies(){
    try {
      const r = await listCompanies(token);
      setCompanies(r.data);
    } catch(e){
      console.error(e);
    }
  }

  function onLogin(tkn){
    setToken(tkn);
  }

  function onUploaded(data){
    setResult(data);
    // refresh companies for user
    loadCompanies();
  }

  if (!token) return <Login onLogin={onLogin} />;

  return (
    <div style={{padding:20}}>
      <div style={{display:"flex", justifyContent:"space-between"}}>
        <div><strong>Balance Sheet Assistant</strong></div>
        <div>
          <button onClick={()=>{ setToken(null); setResult(null); }}>Logout</button>
        </div>
      </div>

      <div style={{display:"flex", gap:20, marginTop:20}}>
        <div style={{flex:1}}>
          <UploadPanel token={token} onUploaded={onUploaded} />
        </div>
        <div style={{flex:2}}>
          <Dashboard result={result} />
        </div>
      </div>

      <div style={{marginTop:20}}>
        <h4>Your companies</h4>
        <ul>{companies.map(c => <li key={c.id}>{c.name} ({c.group})</li>)}</ul>
      </div>
    </div>
  );
}

export default App;
