// frontend/src/components/Dashboard.jsx
import React from "react";
import { LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer } from "recharts";

export default function Dashboard({ result }) {
  if (!result) return <div>No data</div>;
  const ctx = result.numeric_context || {};
  const kpis = result.kpis || {};

  // optionally create timeseries if result provides it
  const timeseries = ctx.timeseries || [];

  return (
    <div>
      <h2>{result.company}</h2>
      <div>
        <strong>Revenue:</strong> {ctx.revenue || "—"}
      </div>
      <div>
        <strong>Total assets:</strong> {ctx.total_assets || "—"}
      </div>
      <div>
        <strong>Debt/Equity:</strong> {kpis.debt_equity ? kpis.debt_equity.toFixed(2) : "—"}
      </div>

      <div style={{height:300}}>
        {timeseries.length > 0 ? (
          <ResponsiveContainer>
            <LineChart data={timeseries}>
              <XAxis dataKey="period" />
              <YAxis />
              <Tooltip />
              <Line dataKey="revenue" />
            </LineChart>
          </ResponsiveContainer>
        ) : (<div>No timeseries</div>)}
      </div>

      <div>
        <h3>LLM Analysis</h3>
        <pre>{JSON.stringify(result.analysis, null, 2)}</pre>
      </div>
    </div>
  );
}
