// frontend/src/api.js
import axios from "axios";
const BACKEND = process.env.REACT_APP_BACKEND_URL || "http://localhost:8000";

export const login = (email, password) => axios.post(`${BACKEND}/auth/login`, { email, password });
export const listCompanies = (token) => axios.get(`${BACKEND}/companies`, { headers: { Authorization: `Bearer ${token}` }});
export const uploadPdf = (token, company_name, file, pdf_url) => {
  const fd = new FormData();
  fd.append("company_name", company_name);
  if (file) fd.append("file", file);
  if (pdf_url) fd.append("pdf_url", pdf_url);
  return axios.post(`${BACKEND}/upload_pdf`, fd, { headers: { Authorization: `Bearer ${token}` }});
};
export const getCompanySheets = (token, company_id) => axios.get(`${BACKEND}/companies/${company_id}/balance_sheets`, { headers: { Authorization: `Bearer ${token}` }});
